import { HttpErrorResponse } from "@angular/common/http";
import { Component, OnInit, ViewChild, ViewContainerRef } from "@angular/core";
import { ProfileComponent } from "../profile/profile.component";

import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from "@angular/forms";
import { Router } from "@angular/router";
import { IAuthorization } from "src/app/interfaces/Authorization";
import { IUserAccounts } from "src/app/interfaces/UserAccounts";
import { AuthorizationService } from "src/app/services/authorization.service";
@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.css"],
})
export class LoginComponent implements OnInit {
  @ViewChild('ProfileModal',{read:ProfileComponent}) 
  contenedor!:ViewContainerRef;
  loginForm: FormGroup;
  credentials: IAuthorization;
  payload:IUserAccounts[] = [];

  constructor(
    private service: AuthorizationService,
    private formBuilder: FormBuilder,
    private navigationApp:Router
  ) {
    this.loginForm = this.formBuilder.group({
      usernameInput: new FormControl("", Validators.required),
      passwordInput: new FormControl("", Validators.required),
    });
    this.credentials = {password:'',username:''}
  }

  async GenerateAuth() {

    this.credentials = {
      username:this.loginForm.get('usernameInput')?.value,
      password:this.loginForm.get('passwordInput')?.value
    }

    this.service.PostAuthorization(this.credentials).subscribe(
      (result: any) => {
        this.payload.pop();
        this.payload.push(result);
        console.log(this.payload);
        localStorage.setItem('payload',JSON.stringify(this.payload))
        switch(this.payload[0].jwt){
          case "USER NOT FOUND":
            alert("THIS USER WAS NOT FOUND OR YOU ENTER BAD CREDENTIALS! PLEASE TRY AGAIN");
            break;
          case "NO AUTHORIZED":
            alert("BAD CREDENTIALS OR USER ACCOUNT INACTIVE, PLEASE TRY AGAIN");
            break
          default:
            this.navigationApp.navigateByUrl('/Menu');
          }
      },
      (error: HttpErrorResponse) => {
        alert(error.error.error.message);
      }
    );
  }

  ngOnInit(): void {}
}
<div class="container bg-dark">
  <div class="row justify-content-center align-items-center vh-100">
    <div class="col-md-4">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title mb-4">Iniciar Sesión</h5>
          <form [formGroup]="loginForm" (ngSubmit)="GenerateAuth()">
            <div class="mb-3">
              <label for="username" class="form-label">Nombre de usuario</label>
              <input
                type="text"
                class="form-control"
                id="username"
                name="username"
                placeholder="Enter Username"
                formControlName="usernameInput"
                required="true"
              />
            </div>
            <div class="mb-3">
              <label for="password" class="form-label">Contraseña</label>
              <input
                type="password"
                class="form-control"
                id="password"
                name="password"
                placeholder="Enter Password"
                formControlName="passwordInput"
                required="true"
              />
            </div>
            <button type="submit" class="btn btn-primary" [disabled]="loginForm.invalid">
              Iniciar sesión
            </button>
          </form>
        </div>
      </div>
    </div>
  </div>

</div>
