import { HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { IAuthorization } from 'src/app/interfaces/Authorization';
import { AuthorizationService } from 'src/app/services/authorization.service';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {


  loginForm:FormGroup;
  credentials:IAuthorization = {password:'',username:''};

  constructor(private service:AuthorizationService,private formBuilder:FormBuilder) {
    this.loginForm = this.formBuilder.group({
      usernameInput: new FormControl('',Validators.required),
      passwordInput: new FormControl('',Validators.required)
    })
   }

  async GenerateAuth(){
    this.credentials.password == this.loginForm.get('usernameInput')?.value
    this.credentials.username == this.loginForm.get('passwordInput')?.value

    console.log(this.credentials);

     this.service.PostAuthorization(this.credentials).subscribe((result:any) =>{
      alert(result.status)
     },(error:HttpErrorResponse) =>{
      alert(error.error.error.message);
     })

  }

  ngOnInit(): void {
  }

}
<div class="container bg-dark">
  <div class="row justify-content-center align-items-center vh-100">
    <div class="col-md-4">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title mb-4">Iniciar Sesión</h5>
          <form [formGroup]="loginForm" (ngSubmit)="GenerateAuth()">
            <div class="mb-3">
              <label for="username" class="form-label">Nombre de usuario</label>
              <input
                type="text"
                class="form-control"
                id="username"
                name="username"
                placeholder="Enter Username"
                formControlName="usernameInput"
                required="true"
              />
            </div>
            <div class="mb-3">
              <label for="password" class="form-label">Contraseña</label>
              <input
                type="password"
                class="form-control"
                id="password"
                name="password"
                placeholder="Enter Password"
                formControlName="passwordInput"
                required="true"
              />
            </div>
            <button type="submit" class="btn btn-primary">
              Iniciar sesión
            </button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
